package com.example.spacex

import android.os.Bundle
import androidx.activity.enableEdgeToEdge
import androidx.appcompat.app.AppCompatActivity
import androidx.core.view.ViewCompat
import androidx.core.view.WindowInsetsCompat
import com.bumptech.glide.Glide
import com.example.spacex.databinding.ActivityDetailBinding
import com.example.spacex.model.Fairings
import java.io.Serializable

class DetailActivity : AppCompatActivity(),Serializable {
    private lateinit var binding: ActivityDetailBinding
    private lateinit var farings:Fairings
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        binding=ActivityDetailBinding.inflate(layoutInflater)
        setContentView(binding.root)
        val bundle= intent.extras!!.getBundle("datos")
        farings=bundle!!.getSerializable("mision") as Fairings
        instancias()
    }

    private fun instancias() {
        Glide.with(this)
            .load("data:image/jpeg;base64,") // Cambia por tu URL o recurso local
            .into(binding.backgroundImageView);
        binding.textoNombreDetail.text=farings.name
        binding.textoDescripcionDetail.text=farings.details
        Glide.with(applicationContext).load(farings.links.patch.small).into(binding.imagenDetalle)
    }
}